class RedMigratorRedmemberUserType extends RedMigrator
{
    public function dataHook($rows)
    {
        $arrFields = array('usergroup_id',
                            'payment_required',
                            'tax_exempt'
                        );

        // Do some custom post processing on the list.
        foreach ($rows as &$row)
        {
            $row = (array) $row;

            // Change fields' name
            if (isset($row['usertype_id']))
            {
                $row['usergroup_id'] = $row['usertype_id'];    
            }

            foreach ($row as $key => $value)
            {
                if (!in_array($key, $arrFields))
                {
                    unset($row[$key]);
                }
            }
        }

        return $rows;
    }
}
?>